 var o = {"_":{"e":[["noun",["An artificial international language, selected by the “Delegation for the Adoption of an Auxillary International Language” (founded at Paris in 1901), made public in 1907, and subsequently greatly revised and extended by a permanent committee or “Academy.” It is a revised and simplified form of Esperanto. It combines systematically the advantages of previous schemes with a thoroughly logical word formation, and has neither accented constants nor arbitrarily coined pronominal words. For each idea that root is selected which is already most international, on the principle of the “greatest facility for the greatest number of people.” The word “Ido” means in the language itself “offspring.” The official name is: “Linguo Internaciona di la Delegitaro (Sistema Ido).”"]]],"a":["idoes"],"c":[],"f":1},"_crase":{"e":[["noun",["Same as Vesuvianite."]]],"a":["idocrases"],"c":["min"],"f":1},"_l":{"e":[["noun",["An image of a divinity; a representation or symbol of a deity or any other being or thing, made or used as an object of worship; a similitude of a false god.","That on which the affections are strongly (often excessively) set; an object of passionate devotion; a person or thing greatly loved or adored.","A false notion or conception; a fallacy."]]],"a":["idols"],"c":[],"f":28},"_later":{"e":[["noun",["A worshiper of idols; one who pays divine honors to images, statues, or representations of anything made by hands; one who worships as a deity that which is not God; a pagan.","An adorer; a great admirer."]]],"a":["idolaters"],"c":[],"f":4},"_latress":{"e":[["noun",["A female worshiper of idols."]]],"a":["idolatresses"],"c":[],"f":1},"_latrize":{"e":[["verb",["To worship idols; to pay idolatrous worship.","To make in idol of; to idolize."]]],"a":["idolatrized","idolatrizing","idolatrizes"],"c":[],"f":0},"_latrous":{"e":[["adje",["Of or pertaining to idolatry; partaking of the nature of idolatry; given to idolatry or the worship of false gods; as, idolatrous sacrifices.","Consisting in, or partaking of, an excessive attachment or reverence; as, an idolatrous veneration for antiquity."]]],"a":[],"c":[],"f":4},"_latrously":{"e":[["adve",["In a idolatrous manner."]]],"a":[],"c":[],"f":0},"_latry":{"e":[["noun",["The worship of idols, images, or anything which is not God; the worship of false gods.","Excessive attachment or veneration for anything; respect or love which borders on adoration."]]],"a":["idolatries"],"c":[],"f":8},"_lize":{"e":[["verb",["To make an idol of; to pay idolatrous worship to; as, to idolize the sacred bull in Egypt.","To love to excess; to love or reverence to adoration; as, to idolize gold, children, a hero.","To practice idolatry."]]],"a":["idolized","idolizing","idolizes"],"c":[],"f":5},"_lizer":{"e":[["noun",["One who idolizes or loves to the point of reverence; an idolater."]]],"a":["idolizers"],"c":[],"f":0},"_loclast":{"e":[["noun",["A breaker of idols; an iconoclast."]]],"a":["idoloclasts"],"c":[],"f":0},"_lographical":{"e":[["adje",["Descriptive of idols."]]],"a":[],"c":[],"f":0},"_lum":{"e":[["noun",["Appearance or image; a phantasm; a spectral image; also, a mental image or idea."]]],"a":["idolums"],"c":[],"f":0},"_neous":{"e":[["adje",["Appropriate; suitable; proper; fit; adequate."]]],"a":[],"c":[],"f":0},"_rgan":{"e":[["noun",["A morphological unit, consisting of two or more plastids, which does not possess the positive character of the person or stock, in distinction from the physiological organ or biorgan. See Morphon."]]],"a":["idorgans"],"c":["biol"],"f":2},"_es":"_ido","_crases":"_idocrase","_ls":"_idol","_laters":"_idolater","_latresses":"_idolatress","_latrized":"_idolatrize","_latrizing":"_idolatrize","_latrizes":"_idolatrize","_latries":"_idolatry","_lized":"_idolize","_lizing":"_idolize","_lizes":"_idolize","_lizers":"_idolizer","_loclasts":"_idoloclast","_lums":"_idolum","_rgans":"_idorgan"};
